mapper.update:
UPDATE MEMBER 
SET PHONE = #{Member_PHONE},
	EMAIL = #{Member_EMAIL}
WHERE #{Member_ID}

UPDATE MEMBER 
SET CITY = #{Member_CITY},
	REGION = #{Member_REGION},
	LOCATION = #{Member_LOCATION}
WHERE #{Member_ID}

UPDATE MEMBER
SET STATUS = 1
WHERE MEMBER_ID = #{MEMBER_ID}

UPDATE MEMBER 
SET PASSWORD = #{MEMBER_PW}
WHERE MEMBER_NO = #{Member_NO}
